  @override
  update() async {
    setState(() {});
    Services imp = new ServiceImp();
    QuerySnapshot<Map<String, dynamic>> collectionProducts =
        await FirebaseFirestore.instance.collection('products').get();
    if (collectionProducts.docs.isEmpty) {
      for (int i = 0; i < productName.length; i++) {
        imp.addproduct(
            name: productName[i],
            price: productPrice[i].toString(),
            id: i,
            path: productImage[i],
            unit: productUnit[i].toString());
      }
    }
    await context.read<MyModel>().getproducts();

    setState(() {});
  }

